import createStore from "unistore"

const initialState ={
    email : '',
    username : '',
    status_login : false,
    avatar : '',
    listMovies : [],
    isLoading : '',
    category : ''
}
export const store = createStore(initialState)

export const actions = store => ({
    handleCategory : async (state,e) =>{
        const keyword = e
        await store.setState({category: keyword})
    }   
})
